<?php

namespace Tests\Feature\Controllers\Api\FeedbackController;

use App\Models\Feedback;
use App\Models\SiteData;
use App\Models\User;
use App\Rules\Api\FeedbackController\Base64;
use Database\Seeders\SettingSeeder;
use Illuminate\Foundation\Testing\RefreshDatabase;
use Illuminate\Foundation\Testing\WithFaker;
use Illuminate\Support\Facades\Auth;
use Illuminate\Support\Facades\Storage;
use Illuminate\Support\Str;
use Laravel\Sanctum\Sanctum;
use Tests\TestCase;

class SendFeedbackTest extends TestCase
{
    use RefreshDatabase;

    public function test_unauthenticated_user_record_feedback()
    {
        $this->seed(SettingSeeder::class);
        $user = User::factory()->create();
        $siteData = SiteData::factory(['user_id'=>$user->id])->create();
        $image = '';

        $headers = [
            'accept'=>'application/json'
        ];
        $data = [
            'email'=>'dilan@asset.digital',
            'comment'=>Str::random(450),
            'screenshot'=>$image,
            'site'=>'test site',
            'site_section'=>'front'
        ];
        $response = $this->post('/api/feedback',$data,$headers);
        $this->assertEquals(401,$response->status());
        $response->assertSeeText('Unauthenticated');
    }
    public function test_authenticated_user_record_feedback_with_invalid_data()
    {
        $this->seed(SettingSeeder::class);
        $user = User::factory()->create();
        $siteData = SiteData::factory(['user_id'=>$user->id])->create();
        Sanctum::actingAs($user);
        Storage::fake('images');
        $image = '';

        $data = [
            'email'=>'1234',
            'comment'=>Str::random(1500),
            'screenshot'=>null,
            'site'=>'test site',
        ];
        $response = $this->post('/api/feedback',$data);
        $response->assertSessionHasErrors(['email','comment','screenshot']);
        $response->assertStatus(302);
    }
    public function test_authenticated_user_record_feedback_successfully()
    {
        $this->seed(SettingSeeder::class);
        $user = User::factory()->create();
        $siteData = SiteData::factory(['user_id'=>$user->id])->create();
        Sanctum::actingAs($user);
        Storage::fake('images');
        $image = '';

        $data = [
            'email'=>'dilan@asset.digital',
            'comment'=>Str::random(450),
            'screenshot'=>$image,
            'site'=>'test site',
            'site_section'=>'front'
        ];
        $response = $this->post('/api/feedback',$data);
        $this->assertDatabaseHas('feedback',[
            'user_id'=>$user->id,
            'site_name'=>$data['site'],
            'site_section'=>$data['site_section'],
            'email'=>$data['email'],
            'comment'=>$data['comment'],
        ]);
        $url = Feedback::where('user_id',$user->id)->first()->screenshot;
        $this->assertNotNull($url);
        $response->assertStatus(200);
        $response->assertSeeText('Feedback recorded');
    }


}
